#include <iostream>
#include <string>
#include <fstream>

using namespace std;

int main() {
    // Open the file for reading
    ifstream file("example.txt", ios::binary);
    // Get the size of the file
    int size = file.tellg();
    // Seek to the end of the file
    file.seekg(size);
    // Read the contents of the file into a string
    string contents;
    file.read((char*)&contents, size);
    // Close the file
    file.close();
    cout << "File contents: " << contents << endl;
    return 0;
}
